(()=>{const loadImg=()=>{const iH=document.documentElement.clientHeight,t=document.documentElement.scrollTop||document.body.scrollTop,imgList=document.querySelectorAll(".loadImg");for(let i=0;i<imgList.length;i++){const el=imgList[i];if(!el.stop&&getOffset(el).top<iH+t&&getOffset(el).bottom>t){el.stop=!0;const img=new Image,src=el.getAttribute("data-url");img.src=src,img.onload=()=>{el.style.backgroundImage=`url(${src})`,el.classList.remove("loading","loadImg")}}}},getOffset=obj=>{for(var t=obj.offsetTop;obj.offsetparent;)t+=(obj=obj.offsetparent).offsetTop;return{top:t,bottom:t+obj.offsetHeight}};loadImg(),window.addEventListener("scroll",()=>{loadImg()})})();